-- ------------------------------------------------------- Table `roledetails`-- -----------------------------------------------------CREATE  TABLE IF NOT EXISTS `roledetails` (  `roleID` INT(11) NOT NULL DEFAULT '0' ,  `module` VARCHAR(30) NOT NULL DEFAULT '' ,  `sub` VARCHAR(30) NOT NULL DEFAULT '' ,  `value` TINYINT(1) NOT NULL DEFAULT '0' ,  PRIMARY KEY (`roleID`, `module`, `sub`) )ENGINE = MyISAMDEFAULT CHARACTER SET = latin1;-- ------------------------------------------------------- Table `roles`-- -----------------------------------------------------CREATE  TABLE IF NOT EXISTS `roles` (  `ID` MEDIUMINT(9) NOT NULL AUTO_INCREMENT ,  `name` VARCHAR(100) NOT NULL DEFAULT '' ,  `sequence` INT(11) NOT NULL ,  PRIMARY KEY (`ID`) )ENGINE = MyISAMAUTO_INCREMENT = 2DEFAULT CHARACTER SET = latin1;-- ------------------------------------------------------- Table `users`-- -----------------------------------------------------CREATE  TABLE IF NOT EXISTS `users` (  `ID` MEDIUMINT(9) NOT NULL AUTO_INCREMENT ,  `username` VARCHAR(50) NOT NULL DEFAULT '' ,  `password` VARCHAR(50) NOT NULL DEFAULT '' ,  `fullname` VARCHAR(128) NOT NULL DEFAULT '' ,  `role` INT(11) NOT NULL DEFAULT '0' ,  PRIMARY KEY (`ID`) ,  UNIQUE INDEX `usr_username` (`username` ASC) )ENGINE = MyISAMAUTO_INCREMENT = 2DEFAULT CHARACTER SET = latin1;-- ------------------------------------------------------- Table `settings`-- -----------------------------------------------------CREATE  TABLE IF NOT EXISTS `settings` (  `name` VARCHAR(50) NOT NULL ,  `value` TEXT NULL ,  PRIMARY KEY (`name`) )ENGINE = InnoDB;-- ------------------------------------------------------- Table `google_accounts`-- -----------------------------------------------------CREATE  TABLE IF NOT EXISTS `google_accounts` (  `identity` VARCHAR(255) NOT NULL ,  `userid` INT NULL ,  PRIMARY KEY (`identity`) )ENGINE = InnoDB;-- ------------------------------------------------------- Dat